import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { User } from 'lucide-react';

const Header = () => {
    const navigate = useNavigate();
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    const userEmail = localStorage.getItem('userEmail');
    const userName = localStorage.getItem('userName') || "User"; // Default Name
    const userCourse = localStorage.getItem('userCourse') || "Course: Not Set"; // Default Course

    const toggleDropdown = () => {
        setDropdownOpen((prev) => !prev);
    };

    const handleLogout = () => {
        localStorage.removeItem('userEmail');
        navigate('/', { replace: true });
        window.location.reload();
    };

    const handleProfilePage = () => {
        navigate('/profile');
    };

    // Close dropdown when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setDropdownOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <div className="flex justify-between items-center p-4 bg-purple-600 text-white">
            {/* Left: Logo & Name */}
            <div className="flex items-center ml-4">
                <img 
                    src="https://via.placeholder.com/40" // Default logo
                    alt="Logo"
                    className="w-10 h-10 rounded-full mr-2"
                />
                <span className="text-xl font-bold">{userName}</span>
            </div>

            {/* Center: Course Name */}
            <div className="text-2xl font-bold">{userCourse}</div>

            {/* Right: User Icon & Dropdown */}
            <div ref={dropdownRef} className="relative mr-4">
                <div className="cursor-pointer" onClick={toggleDropdown}>
                    <User size={40} className="text-white" />
                </div>

                {dropdownOpen && (
                    <div className="absolute right-0 mt-2 w-48 bg-white text-purple-600 rounded shadow-lg z-10">
                        <div className="px-4 py-2 hover:bg-gray-200 cursor-pointer" onClick={handleProfilePage}>
                            Profile
                        </div>
                        <div className="px-4 py-2 hover:bg-gray-200 cursor-pointer" onClick={handleLogout}>
                            Logout
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Header;
