import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { User, GraduationCap } from 'lucide-react';

const Header = () => {
    const navigate = useNavigate();
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    const userEmail = localStorage.getItem('userEmail');
    const [userData, setUserData] = useState({ name: "User", course: "Not Set" });

    useEffect(() => {
        const fetchUserData = async () => {
            if (!userEmail) return;

            try {
                const response = await fetch(`http://localhost:5000/get-user?email=${userEmail}`);
                const result = await response.json();
                if (response.ok && result.success) {
                    setUserData({ name: result.user.name, course: result.user.course });
                }
            } catch (error) {
                console.error("Error fetching user data:", error);
            }
        };

        fetchUserData();
    }, [userEmail]);

    const toggleDropdown = () => {
        setDropdownOpen((prev) => !prev);
    };

    const handleLogout = () => {
        localStorage.removeItem('userEmail');
        navigate('/', { replace: true });
        window.location.reload();
    };

    const handleProfilePage = () => {
        navigate('/profile');
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setDropdownOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <div className="flex justify-between items-center p-4 bg-purple-600 text-white">
            {/* Left Side: Course Name */}
            <div className="text-2xl font-bold flex items-center space-x-2 ml-4">
                <GraduationCap size={28} className="text-white" />
                <span>{userCourse}</span>
            </div>

            {/* Right Side: User Name & Icon */}
            <div className="flex items-center space-x-4">
                <span className="text-lg font-semibold">{userName}</span>
                <div ref={dropdownRef} className="relative">
                    <div className="cursor-pointer" onClick={toggleDropdown}>
                        <User size={40} className="text-white" />
                    </div>

                    {dropdownOpen && (
                        <div className="absolute right-0 mt-2 w-48 bg-white text-purple-600 rounded shadow-lg z-10">
                            <div className="px-4 py-2 hover:bg-gray-200 cursor-pointer" onClick={handleProfilePage}>
                                Profile
                            </div>
                            <div className="px-4 py-2 hover:bg-gray-200 cursor-pointer" onClick={handleLogout}>
                                Logout
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Header;
