import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Clock, ListOrdered, ShieldAlert, Loader2 } from 'lucide-react';
import Header from './Header';

const TestPage = () => {
  const navigate = useNavigate();
  const chapter = localStorage.getItem('currentChapter');

  const [emojiIndex, setEmojiIndex] = useState(0);
  const emojis = ["â³", "ðŸ”„", "ðŸ“", "âš¡", "âœ…"];

  useEffect(() => {
    const interval = setInterval(() => {
      setEmojiIndex((prev) => (prev + 1) % emojis.length);
    }, 1000); // Change emoji every second

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-purple-800 text-white p-6 flex flex-col items-center">
      <Header />
      <div className="mt-16 max-w-3xl mx-auto bg-white bg-opacity-15 rounded-lg p-8 shadow-lg backdrop-blur-md">
        <h2 className="text-4xl font-bold mb-6 text-center">ðŸ“– Test Instructions</h2>

        <div className="space-y-4 text-lg">
          <p className="flex items-center">
            <ListOrdered className="h-6 w-6 mr-2 text-yellow-300" /> The test consists of <b>25 questions</b>.
          </p>
          <p className="flex items-center">
            <Clock className="h-6 w-6 mr-2 text-blue-300" /> You have <b>1 hour</b> to complete the test.
          </p>
          <p className="flex items-center">
            <ShieldAlert className="h-6 w-6 mr-2 text-red-400" /> The test is <b>proctored</b>. Please do not cheat! ðŸš«
          </p>
        </div>

        <div className="flex justify-center mt-8">
          <button className="bg-green-500 hover:bg-green-600 text-white font-bold py-3 px-6 rounded-lg shadow-md flex items-center space-x-3">
            <Loader2 className="h-5 w-5 animate-spin" />
            <span>Preparing Test... {emojis[emojiIndex]}</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default TestPage;
