import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import Header from './Header';

const TopicPage = () => {
  const { chapterName, topicName } = useParams();
  const navigate = useNavigate();

  const [content, setContent] = useState('');
  const [enhancedContent, setEnhancedContent] = useState('');
  const [summary, setSummary] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [error, setError] = useState(null);
  const [courseTitle, setCourseTitle] = useState('');
  const [chapterTitle, setChapterTitle] = useState('');
  const [topicTitle, setTopicTitle] = useState('');
  const [showSummary, setShowSummary] = useState(false);

  // Get the user email from localStorage (as in your chapters page)
  const userEmail = localStorage.getItem('userEmail');

  useEffect(() => {
    // Check if user is logged in
    if (!userEmail) {
      navigate('/login');
      return;
    }

    // Fetch topic content on component mount
    setIsLoading(true);
    fetch('http://localhost:5000/get-topic-content', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        email: userEmail,
        chapterName: decodeURIComponent(chapterName),
        topicName: decodeURIComponent(topicName)
      })
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to fetch topic content');
        }
        return res.json();
      })
      .then((data) => {
        setContent(data.topicContent);
        setEnhancedContent(data.enhancedContent);
        setCourseTitle(data.courseTitle);
        setChapterTitle(data.chapterTitle);
        setTopicTitle(data.topicTitle.replace('.txt', ''));
        setIsLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching topic content:', err);
        setError(err.message || 'An error occurred while fetching topic content');
        setIsLoading(false);
      });
  }, [userEmail, chapterName, topicName, navigate]);

  const handleSummarize = () => {
    // If we already have a summary, just toggle its visibility
    if (summary) {
      setShowSummary(!showSummary);
      return;
    }

    // Otherwise, fetch a new summary
    setIsSummarizing(true);
    fetch('http://localhost:5000/summarize-content', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        content: enhancedContent
      })
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to generate summary');
        }
        return res.json();
      })
      .then((data) => {
        setSummary(data.summary);
        setShowSummary(true);
        setIsSummarizing(false);
      })
      .catch((err) => {
        console.error('Error generating summary:', err);
        setError(err.message || 'An error occurred while generating summary');
        setIsSummarizing(false);
      });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
        <Header />
        <div className="flex justify-center items-center h-96">
          <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-white"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
        <Header />
        <div className="mt-16 max-w-3xl mx-auto">
          <div className="bg-white bg-opacity-10 rounded-lg p-6">
            <h2 className="text-2xl font-bold mb-4">Error</h2>
            <p>{error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
      <Header />
      <div className="mt-12 max-w-4xl mx-auto">
        {/* Breadcrumb navigation */}
        <div className="flex items-center mb-4 text-sm">
          <span 
            className="hover:underline cursor-pointer" 
            onClick={() => navigate('/chapters')}
          >
            Chapters
          </span>
          <span className="mx-2">›</span>
          <span 
            className="hover:underline cursor-pointer" 
            onClick={() => navigate('/chapters')}
          >
            {chapterTitle}
          </span>
          <span className="mx-2">›</span>
          <span>{topicTitle}</span>
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white bg-opacity-10 rounded-lg p-6 shadow-lg"
        >
          <h1 className="text-3xl font-bold mb-2">{topicTitle}</h1>
          <h2 className="text-xl opacity-80 mb-6">{courseTitle} › {chapterTitle}</h2>

          <div className="prose prose-invert max-w-none">
            {/* Display content with proper formatting */}
            <div className="whitespace-pre-line">
              {enhancedContent}
            </div>
          </div>

          {/* Summary section that shows up when requested */}
          {showSummary && summary && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="mt-6 p-4 bg-white bg-opacity-20 rounded-lg"
            >
              <h3 className="text-xl font-semibold mb-2">Summary</h3>
              <div className="whitespace-pre-line">
                {summary}
              </div>
            </motion.div>
          )}

          {/* Summarize button */}
          <div className="mt-8 flex justify-center">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleSummarize}
              disabled={isSummarizing}
              className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white font-bold py-3 px-6 rounded-full shadow-lg transition-all focus:outline-none"
            >
              {isSummarizing ? (
                <div className="flex items-center space-x-2">
                  <div className="animate-spin h-5 w-5 border-2 border-t-transparent border-white rounded-full"></div>
                  <span>Summarizing...</span>
                </div>
              ) : summary ? (
                showSummary ? "Hide Summary" : "Show Summary"
              ) : (
                "Summarize"
              )}
            </motion.button>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default TopicPage;
