import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { ChevronLeft, BookOpen, FileText, Clock } from 'lucide-react';
import Header from './Header';

const TopicPage = () => {
  const { chapterName, topicName } = useParams();
  const navigate = useNavigate();

  // States for content and UI
  const [content, setContent] = useState('');
  const [enhancedContent, setEnhancedContent] = useState('');
  const [summary, setSummary] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [error, setError] = useState(null);
  const [courseTitle, setCourseTitle] = useState('');
  const [chapterTitle, setChapterTitle] = useState('');
  const [topicTitle, setTopicTitle] = useState('');
  const [showSummary, setShowSummary] = useState(false);
  const [fetchRetries, setFetchRetries] = useState(0);

  // Get the user email from localStorage
  const userEmail = localStorage.getItem('userEmail');

  // API base URL - use environment variable if available or default to localhost
  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000';

  const fetchTopicContent = async () => {
    try {
      setIsLoading(true);
      setError(null);

      console.log('Fetching topic content with params:', {
        email: userEmail,
        chapterName: decodeURIComponent(chapterName),
        topicName: decodeURIComponent(topicName)
      });

      const response = await fetch(`${API_BASE_URL}/get-topic-content`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: userEmail,
          chapterName: decodeURIComponent(chapterName),
          topicName: decodeURIComponent(topicName)
        }),
        // Add these options to ensure credentials are included and CORS issues are minimized
        credentials: 'include',
        mode: 'cors'
      });

      if (!response.ok) {
        console.error('API Error:', response.status, response.statusText);
        throw new Error(`Server responded with ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('Topic content fetched successfully:', data);

      if (data.error) {
        throw new Error(data.error);
      }

      setContent(data.topicContent);
      setEnhancedContent(data.enhancedContent);
      setCourseTitle(data.courseTitle);
      setChapterTitle(data.chapterTitle);
      setTopicTitle(data.topicTitle.replace('.txt', ''));
    } catch (err) {
      console.error('Error fetching topic content:', err);
      setError(`Failed to load content: ${err.message}`);

      // Retry logic - maximum 3 retries
      if (fetchRetries < 3) {
        console.log(`Retrying fetch (${fetchRetries + 1}/3)...`);
        setFetchRetries(fetchRetries + 1);
        setTimeout(fetchTopicContent, 1000); // Retry after 1 second
      }
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    // Check if user is logged in
    if (!userEmail) {
      console.log('No user email found, redirecting to login');
      navigate('/login');
      return;
    }

    // Fetch content when component mounts
    fetchTopicContent();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userEmail, navigate]);

  const handleSummarize = async () => {
    // If we already have a summary, just toggle its visibility
    if (summary) {
      setShowSummary(!showSummary);
      return;
    }

    try {
      setIsSummarizing(true);
      setError(null);

      console.log('Requesting summary for content');

      const response = await fetch(`${API_BASE_URL}/summarize-content`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          content: enhancedContent
        }),
        credentials: 'include',
        mode: 'cors'
      });

      if (!response.ok) {
        console.error('API Error:', response.status, response.statusText);
        throw new Error(`Summary generation failed: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('Summary generated successfully');

      if (data.error) {
        throw new Error(data.error);
      }

      setSummary(data.summary);
      setShowSummary(true);
    } catch (err) {
      console.error('Error generating summary:', err);
      setError(`Failed to generate summary: ${err.message}`);

      // Fallback: create a simple summary if the API fails
      if (!summary) {
        const fallbackSummary = `We couldn't generate a full summary at this time. 

The topic "${topicTitle}" covers key concepts from the ${courseTitle} course. 

Please try again later or read through the full content above.`;
        setSummary(fallbackSummary);
        setShowSummary(true);
      }
    } finally {
      setIsSummarizing(false);
    }
  };

  const handleRetry = () => {
    setFetchRetries(0);
    fetchTopicContent();
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
        <Header />
        <div className="flex flex-col justify-center items-center h-96">
          <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-white mb-4"></div>
          <p className="text-xl">Loading content...</p>
        </div>
      </div>
    );
  }

  if (error && !content) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
        <Header />
        <div className="mt-16 max-w-3xl mx-auto">
          <div className="bg-white bg-opacity-10 rounded-lg p-6 shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Oops! Something went wrong</h2>
            <p className="mb-4">{error}</p>
            <div className="flex space-x-4">
              <button 
                onClick={handleRetry} 
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Try Again
              </button>
              <button 
                onClick={() => navigate('/chapters')} 
                className="bg-transparent border border-white hover:bg-white hover:bg-opacity-10 text-white font-bold py-2 px-4 rounded"
              >
                Back to Chapters
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
      <Header />
      <div className="mt-12 max-w-4xl mx-auto">
        {/* Back button */}
        <motion.button
          whileHover={{ scale: 1.03 }}
          whileTap={{ scale: 0.97 }}
          onClick={() => navigate('/chapters')}
          className="mb-6 flex items-center text-blue-100 hover:text-white transition-colors"
        >
          <ChevronLeft className="mr-1 h-5 w-5" />
          Back to Chapters
        </motion.button>

        {/* Breadcrumb navigation */}
        <div className="flex items-center mb-6 text-sm bg-white bg-opacity-10 p-2 rounded-lg overflow-x-auto">
          <BookOpen className="h-4 w-4 mr-2 flex-shrink-0" />
          <span 
            className="hover:underline cursor-pointer whitespace-nowrap" 
            onClick={() => navigate('/chapters')}
          >
            {courseTitle || 'Course'}
          </span>
          <span className="mx-2">›</span>
          <span 
            className="hover:underline cursor-pointer whitespace-nowrap" 
            onClick={() => navigate('/chapters')}
          >
            {chapterTitle || 'Chapter'}
          </span>
          <span className="mx-2">›</span>
          <span className="font-medium whitespace-nowrap">{topicTitle || 'Topic'}</span>
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white bg-opacity-10 rounded-lg p-6 shadow-lg"
        >
          <div className="flex items-center mb-4">
            <FileText className="h-6 w-6 mr-2 flex-shrink-0" />
            <h1 className="text-3xl font-bold break-words">{topicTitle || 'Topic Content'}</h1>
          </div>

          {error && (
            <div className="mb-4 p-3 bg-red-500 bg-opacity-20 border border-red-400 rounded-lg text-white">
              <p className="font-medium">{error}</p>
              <button 
                onClick={handleRetry}
                className="mt-2 text-sm underline hover:no-underline"
              >
                Try Again
              </button>
            </div>
          )}

          <div className="prose prose-invert max-w-none">
            {/* Display content with proper formatting */}
            <div className="whitespace-pre-line bg-white bg-opacity-5 p-4 rounded-lg">
              {enhancedContent || 'No content available.'}
            </div>
          </div>

          {/* Summary section that shows up when requested */}
          {showSummary && summary && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="mt-8 p-6 bg-blue-800 bg-opacity-50 rounded-lg border border-blue-400 border-opacity-30"
            >
              <div className="flex items-center mb-3">
                <Clock className="h-5 w-5 mr-2" />
                <h3 className="text-xl font-semibold">Summary</h3>
              </div>
              <div className="whitespace-pre-line">
                {summary}
              </div>
            </motion.div>
          )}

          {/* Summarize button */}
          <div className="mt-8 flex justify-center">
            <motion.button
              whileHover={{ scale: 1.03 }}
              whileTap={{ scale: 0.97 }}
              onClick={handleSummarize}
              disabled={isSummarizing}
              className={`
                flex items-center justify-center
                px-8 py-3 rounded-full shadow-lg 
                font-bold transition-all focus:outline-none
                ${isSummarizing ? 
                  'bg-blue-700 bg-opacity-50 cursor-not-allowed' : 
                  'bg-gradient-to-r from-blue-400 to-purple-500 hover:from-blue-500 hover:to-purple-600'}
              `}
            >
              {isSummarizing ? (
                <div className="flex items-center space-x-2">
                  <div className="animate-spin h-5 w-5 border-2 border-t-transparent border-white rounded-full"></div>
                  <span>Summarizing...</span>
                </div>
              ) : summary ? (
                <div className="flex items-center">
                  <Clock className="mr-2 h-5 w-5" />
                  {showSummary ? "Hide Summary" : "Show Summary"}
                </div>
              ) : (
                <div className="flex items-center">
                  <Clock className="mr-2 h-5 w-5" />
                  Summarize Content
                </div>
              )}
            </motion.button>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default TopicPage;
