import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { ChevronLeft, BookOpen, FileText, Clock } from 'lucide-react';
import Header from './Header';

const TopicPage = () => {
  const { chapterName, topicName } = useParams();
  const navigate = useNavigate();

  const [content, setContent] = useState('');
  const [enhancedContent, setEnhancedContent] = useState('');
  const [summary, setSummary] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [error, setError] = useState(null);
  const [courseTitle, setCourseTitle] = useState('');
  const [chapterTitle, setChapterTitle] = useState('');
  const [topicTitle, setTopicTitle] = useState('');
  const [showSummary, setShowSummary] = useState(false);

  // Get the user email from localStorage (as in your chapters page)
  const userEmail = localStorage.getItem('userEmail');

  useEffect(() => {
    // Check if user is logged in
    if (!userEmail) {
      navigate('/login');
      return;
    }

    // Fetch topic content on component mount
    setIsLoading(true);
    fetch('http://localhost:5000/get-topic-content', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        email: userEmail,
        chapterName: decodeURIComponent(chapterName),
        topicName: decodeURIComponent(topicName)
      })
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to fetch topic content');
        }
        return res.json();
      })
      .then((data) => {
        setContent(data.topicContent);
        setEnhancedContent(data.enhancedContent);
        setCourseTitle(data.courseTitle);
        setChapterTitle(data.chapterTitle);
        setTopicTitle(data.topicTitle.replace('.txt', ''));
        setIsLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching topic content:', err);
        setError(err.message || 'An error occurred while fetching topic content');
        setIsLoading(false);
      });
  }, [userEmail, chapterName, topicName, navigate]);

  const handleSummarize = () => {
    // If we already have a summary, just toggle its visibility
    if (summary) {
      setShowSummary(!showSummary);
      return;
    }

    // Otherwise, fetch a new summary
    setIsSummarizing(true);
    fetch('http://localhost:5000/summarize-content', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        content: enhancedContent
      })
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to generate summary');
        }
        return res.json();
      })
      .then((data) => {
        setSummary(data.summary);
        setShowSummary(true);
        setIsSummarizing(false);
      })
      .catch((err) => {
        console.error('Error generating summary:', err);
        setError(err.message || 'An error occurred while generating summary');
        setIsSummarizing(false);
      });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
        <Header />
        <div className="flex justify-center items-center h-96">
          <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-white"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
        <Header />
        <div className="mt-16 max-w-3xl mx-auto">
          <div className="bg-white bg-opacity-10 rounded-lg p-6">
            <h2 className="text-2xl font-bold mb-4">Error</h2>
            <p>{error}</p>
            <button 
              onClick={() => navigate('/chapters')} 
              className="mt-4 flex items-center text-blue-200 hover:text-white"
            >
              <ChevronLeft className="mr-1 h-5 w-5" />
              Back to Chapters
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 text-white p-6">
      <Header />
      <div className="mt-12 max-w-4xl mx-auto">
        {/* Back button */}
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => navigate('/chapters')}
          className="mb-6 flex items-center text-blue-100 hover:text-white transition-colors"
        >
          <ChevronLeft className="mr-1 h-5 w-5" />
          Back to Chapters
        </motion.button>

        {/* Breadcrumb navigation */}
        <div className="flex items-center mb-6 text-sm bg-white bg-opacity-10 p-2 rounded-lg">
          <BookOpen className="h-4 w-4 mr-2" />
          <span 
            className="hover:underline cursor-pointer" 
            onClick={() => navigate('/chapters')}
          >
            {courseTitle}
          </span>
          <span className="mx-2">›</span>
          <span 
            className="hover:underline cursor-pointer" 
            onClick={() => navigate('/chapters')}
          >
            {chapterTitle}
          </span>
          <span className="mx-2">›</span>
          <span className="font-medium">{topicTitle}</span>
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white bg-opacity-10 rounded-lg p-6 shadow-lg"
        >
          <div className="flex items-center mb-4">
            <FileText className="h-6 w-6 mr-2" />
            <h1 className="text-3xl font-bold">{topicTitle}</h1>
          </div>

          <div className="prose prose-invert max-w-none">
            {/* Display content with proper formatting */}
            <div className="whitespace-pre-line bg-white bg-opacity-5 p-4 rounded-lg">
              {enhancedContent}
            </div>
          </div>

          {/* Summary section that shows up when requested */}
          {showSummary && summary && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="mt-8 p-6 bg-blue-800 bg-opacity-50 rounded-lg border border-blue-400 border-opacity-30"
            >
              <div className="flex items-center mb-3">
                <Clock className="h-5 w-5 mr-2" />
                <h3 className="text-xl font-semibold">Summary</h3>
              </div>
              <div className="whitespace-pre-line">
                {summary}
              </div>
            </motion.div>
          )}

          {/* Summarize button - fixed at the bottom */}
          <div className="mt-8 flex justify-center">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleSummarize}
              disabled={isSummarizing}
              className={`
                flex items-center justify-center
                px-8 py-3 rounded-full shadow-lg 
                font-bold transition-all focus:outline-none
                ${isSummarizing ? 
                  'bg-blue-700 bg-opacity-50 cursor-not-allowed' : 
                  'bg-gradient-to-r from-blue-400 to-purple-500 hover:from-blue-500 hover:to-purple-600'}
              `}
            >
              {isSummarizing ? (
                <div className="flex items-center space-x-2">
                  <div className="animate-spin h-5 w-5 border-2 border-t-transparent border-white rounded-full"></div>
                  <span>Summarizing...</span>
                </div>
              ) : summary ? (
                <div className="flex items-center">
                  <Clock className="mr-2 h-5 w-5" />
                  {showSummary ? "Hide Summary" : "Show Summary"}
                </div>
              ) : (
                <div className="flex items-center">
                  <Clock className="mr-2 h-5 w-5" />
                  Summarize Content
                </div>
              )}
            </motion.button>
          </div>
        </motion.div>

        {/* Fixed floating button for summarize when scrolling */}
        {!showSummary && enhancedContent.length > 1000 && (
          <motion.button
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1 }}
            onClick={handleSummarize}
            disabled={isSummarizing}
            className={`
              fixed bottom-8 right-8
              flex items-center justify-center
              p-4 rounded-full shadow-lg 
              bg-gradient-to-r from-blue-400 to-purple-500 hover:from-blue-500 hover:to-purple-600
              transition-all focus:outline-none
              ${isSummarizing ? 'cursor-not-allowed' : ''}
            `}
          >
            {isSummarizing ? (
              <div className="animate-spin h-6 w-6 border-2 border-t-transparent border-white rounded-full"></div>
            ) : (
              <Clock className="h-6 w-6" />
            )}
          </motion.button>
        )}
      </div>
    </div>
  );
};

export default TopicPage;
