import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { User } from 'lucide-react';

const Header = () => {
  const navigate = useNavigate();
  const [isHovered, setIsHovered] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('userEmail');
    navigate('/login');
  };

  const handleProfilePage = () => {
    navigate('/profile');
  };

  return (
    <div className="flex justify-end items-center relative">
      {/* User Icon - Hover to show dropdown */}
      <div 
        className="cursor-pointer relative"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setTimeout(() => setIsHovered(false), 200)} // Delay before hiding
      >
        <User size={40} className="text-white" />

        {/* Dropdown should stay visible when hovered */}
        {isHovered && (
          <div 
            className="absolute right-0 mt-2 w-48 bg-white text-purple-600 rounded shadow-lg z-10"
            onMouseEnter={() => setIsHovered(true)}  // Prevent hiding when moving to dropdown
            onMouseLeave={() => setTimeout(() => setIsHovered(false), 200)}
          >
            <div className="px-4 py-2 hover:bg-gray-200 cursor-pointer" onClick={handleProfilePage}>
              Profile
            </div>
            <div className="px-4 py-2 hover:bg-gray-200 cursor-pointer" onClick={handleLogout}>
              Logout
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Header;
