import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { User } from 'lucide-react';
import { motion } from 'framer-motion';

const Header = () => {
    const navigate = useNavigate();
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    const userEmail = localStorage.getItem('userEmail');
    const userName = localStorage.getItem('userName');
    const userCourse = localStorage.getItem('userCourse');

    const toggleDropdown = () => {
        setDropdownOpen((prev) => !prev);
    };

    const handleLogout = () => {
        localStorage.removeItem('userEmail');
        navigate('/', { replace: true });
        window.location.reload();
    };

    const handleProfilePage = () => {
        navigate('/profile');
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setDropdownOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <div className="flex justify-between items-center p-4 bg-gradient-to-r from-stone-700 to-stone-600 text-amber-50 font-serif">
            {/* Course Info */}
            <motion.div 
                whileHover={{ scale: 1.02 }}
                className="text-xl font-bold ml-4 bg-stone-800 bg-opacity-50 px-4 py-2 rounded-lg shadow-md"
            >
                {userCourse ? `Course: ${userCourse}` : "Course: Not Set"}
            </motion.div>

            {/* User Info & Dropdown */}
            <div ref={dropdownRef} className="relative flex items-center mr-4">
                {userName && (
                    <motion.span 
                        // whileHover={{ scale: 1.05 }}
                        className="mr-3 text-lg bg-stone-800 bg-opacity-50 px-3 py-1 rounded-lg shadow-md"
                    >
                        {userName}
                    </motion.span>
                )}

                <motion.div 
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.95 }}
                    className="cursor-pointer" 
                    onClick={toggleDropdown}
                >
                    <User size={36} className="text-amber-50 bg-stone-800 bg-opacity-50 p-1 rounded-full shadow-md" />
                </motion.div>

                {dropdownOpen && (
                    <motion.div 
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="absolute right-0 mt-2 w-48 bg-white bg-opacity-90 text-stone-800 rounded-lg shadow-lg z-10 border border-stone-300 backdrop-blur-sm"
                    >
                        <motion.div 
                            whileHover={{ scale: 1.02, backgroundColor: 'rgba(245, 245, 244, 0.9)' }}
                            className="px-4 py-2 cursor-pointer rounded-t-lg"
                            onClick={handleProfilePage}
                        >
                            Profile
                        </motion.div>
                        <motion.div 
                            whileHover={{ scale: 1.02, backgroundColor: 'rgba(245, 245, 244, 0.9)' }}
                            className="px-4 py-2 cursor-pointer rounded-b-lg"
                            onClick={handleLogout}
                        >
                            Logout
                        </motion.div>
                    </motion.div>
                )}
            </div>
        </div>
    );
};

export default Header;
