import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { User, BookOpen, Calendar, Award, CheckCircle, XCircle, Upload, ChevronLeft } from 'lucide-react';
import Header from './Header';

const ProfilePage = () => {
  const navigate = useNavigate();
  const userEmail = localStorage.getItem('userEmail');
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/get-user-data?email=${userEmail}`);
        const data = await response.json();

        if (response.ok) {
          setUserData(data);
          if (data.profileImageUrl) {
            setPreviewUrl(data.profileImageUrl);
          }
        } else {
          console.error('Error fetching user data:', data.error);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [userEmail]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);

      // Create preview URL
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setUploading(true);

    try {
      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('email', userEmail);

      const response = await fetch('http://localhost:5000/upload-profile-image', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (response.ok) {
        setUserData(prev => ({ ...prev, profileImageUrl: data.imageUrl }));
      } else {
        console.error('Error uploading image:', data.error);
      }
    } catch (error) {
      console.error('Error uploading image:', error);
    } finally {
      setUploading(false);
    }
  };

  const getInitials = (name) => {
    if (!name) return '';
    const names = name.split(' ');
    let initials = names[0].substring(0, 1).toUpperCase();

    if (names.length > 1) {
      initials += names[names.length - 1].substring(0, 1).toUpperCase();
    }

    return initials;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-amber-50 to-stone-100 font-serif flex items-center justify-center">
        <div className="text-stone-800 text-xl">Loading profile...</div>
      </div>
    );
  }

  if (!userData) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-amber-50 to-stone-100 font-serif flex items-center justify-center">
        <div className="text-stone-800 text-xl">Error loading profile data</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-50 to-stone-100 font-serif relative">
      {/* Background pattern */}
      <div className="fixed inset-0 overflow-hidden opacity-10 -z-10 pointer-events-none">
        {[...Array(15)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute rounded-full bg-stone-400"
            style={{
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
              width: `${Math.random() * 60 + 20}px`,
              height: `${Math.random() * 60 + 20}px`,
            }}
            animate={{
              y: [0, Math.random() * 50 - 25],
              opacity: [0.05, 0.1, 0.05],
            }}
            transition={{
              duration: Math.random() * 10 + 15,
              repeat: Infinity,
              repeatType: "reverse",
            }}
          />
        ))}
      </div>

      <Header />

      <div className="pt-24 pb-12 px-6 max-w-6xl mx-auto">
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="mb-6 flex items-center text-stone-800"
          onClick={() => navigate(-1)}
        >
          <ChevronLeft className="h-5 w-5 mr-2" />
          Back
        </motion.button>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left Column - Profile Info */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white/70 rounded-xl shadow-lg p-8 border border-stone-300/50 backdrop-blur-sm lg:col-span-1"
          >
            <div className="flex flex-col items-center mb-6">
              {/* Profile Image */}
              <div className="relative mb-4">
                {previewUrl ? (
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    className="w-32 h-32 rounded-full bg-stone-200 flex items-center justify-center overflow-hidden border-2 border-stone-300"
                  >
                    <img 
                      src={previewUrl} 
                      alt="Profile" 
                      className="w-full h-full object-cover"
                    />
                  </motion.div>
                ) : (
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    className="w-32 h-32 rounded-full bg-stone-700 flex items-center justify-center text-4xl text-amber-50 font-bold"
                  >
                    {getInitials(userData.name)}
                  </motion.div>
                )}
              </div>

              {/* Upload Section */}
              <div className="text-center">
                <label className="cursor-pointer">
                  <input 
                    type="file" 
                    className="hidden" 
                    accept="image/*"
                    onChange={handleFileChange}
                  />
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="bg-stone-700/20 text-stone-800 px-4 py-2 rounded-full flex items-center justify-center border border-stone-700/30 mb-2"
                  >
                    <Upload className="h-4 w-4 mr-2" />
                    Change Photo
                  </motion.div>
                </label>
                {selectedFile && !uploading && (
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handleUpload}
                    className="bg-gradient-to-r from-stone-700 to-stone-600 text-amber-50 px-4 py-2 rounded-full text-sm"
                  >
                    Save Changes
                  </motion.button>
                )}
                {uploading && (
                  <div className="text-stone-600 text-sm">Uploading...</div>
                )}
              </div>
            </div>

            {/* User Info */}
            <div className="space-y-4">
              <div className="flex items-center">
                <User className="h-5 w-5 mr-3 text-stone-700" />
                <div>
                  <div className="text-sm text-stone-600">Name</div>
                  <div className="text-lg font-medium text-stone-800">{userData.name}</div>
                </div>
              </div>

              <div className="flex items-center">
                <BookOpen className="h-5 w-5 mr-3 text-stone-700" />
                <div>
                  <div className="text-sm text-stone-600">Course</div>
                  <div className="text-lg font-medium text-stone-800">
                    {userData.course || 'Not specified'}
                  </div>
                </div>
              </div>

              <div className="flex items-center">
                <Calendar className="h-5 w-5 mr-3 text-stone-700" />
                <div>
                  <div className="text-sm text-stone-600">Date of Birth</div>
                  <div className="text-lg font-medium text-stone-800">
                    {userData.dob || 'Not specified'}
                  </div>
                </div>
              </div>

              <div className="flex items-center">
                <Award className="h-5 w-5 mr-3 text-stone-700" />
                <div>
                  <div className="text-sm text-stone-600">Current Rank</div>
                  <div className="text-lg font-medium text-stone-800">
                    {userData.rank || 1}
                  </div>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Right Column - Performance Data */}
          <div className="lg:col-span-2 space-y-8">
            {/* Unlocked Chapters */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 }}
              className="bg-white/70 rounded-xl shadow-lg p-8 border border-stone-300/50 backdrop-blur-sm"
            >
              <h2 className="text-2xl font-bold mb-6 flex items-center text-stone-800">
                <BookOpen className="h-6 w-6 mr-3" />
                Unlocked Chapters
              </h2>

              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {[1, 2, 3, 4, 5].map((chapter) => (
                  <motion.div
                    key={chapter}
                    whileHover={{ y: -5 }}
                    className={`p-4 rounded-lg text-center ${
                      userData.rank >= chapter
                        ? 'bg-stone-700/10 border border-stone-700/30'
                        : 'bg-stone-100/50 border border-stone-300'
                    }`}
                  >
                    <div className="text-lg font-bold text-stone-800 mb-1">
                      Chapter {chapter}
                    </div>
                    <div className="text-sm text-stone-600">
                      {userData.rank >= chapter ? 'Unlocked' : `Unlocks at rank ${chapter}`}
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Strong Topics */}
            {userData.performance_data && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2 }}
                className="bg-white/70 rounded-xl shadow-lg p-8 border border-stone-300/50 backdrop-blur-sm"
              >
                <h2 className="text-2xl font-bold mb-6 flex items-center text-stone-800">
                  <CheckCircle className="h-6 w-6 mr-3 text-green-600" />
                  Your Strong Topics
                </h2>

                {Object.values(userData.performance_data).some(chapter => 
                  chapter.strong_topics && chapter.strong_topics.length > 0
                ) ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {Object.entries(userData.performance_data).map(([chapterNum, chapterData]) => (
                      chapterData.strong_topics && chapterData.strong_topics.length > 0 && (
                        <div key={chapterNum} className="mb-4">
                          <h3 className="font-bold text-stone-700 mb-2">Chapter {chapterNum}</h3>
                          <ul className="space-y-2">
                            {chapterData.strong_topics.map((topic, index) => (
                              <motion.li
                                key={index}
                                whileHover={{ x: 5 }}
                                className="bg-green-50/70 p-3 rounded-lg border border-green-200/50"
                              >
                                <div className="font-medium text-green-700">{topic.topic}</div>
                                <div className="text-sm text-stone-600">
                                  {topic.details.join(', ')}
                                </div>
                              </motion.li>
                            ))}
                          </ul>
                        </div>
                      )
                    ))}
                  </div>
                ) : (
                  <p className="text-stone-600 italic">No strong topics identified yet</p>
                )}
              </motion.div>
            )}

            {/* Weak Topics */}
            {userData.performance_data && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
                className="bg-white/70 rounded-xl shadow-lg p-8 border border-stone-300/50 backdrop-blur-sm"
              >
                <h2 className="text-2xl font-bold mb-6 flex items-center text-stone-800">
                  <XCircle className="h-6 w-6 mr-3 text-red-600" />
                  Areas for Improvement
                </h2>

                {Object.values(userData.performance_data).some(chapter => 
                  chapter.weak_topics && chapter.weak_topics.length > 0
                ) ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {Object.entries(userData.performance_data).map(([chapterNum, chapterData]) => (
                      chapterData.weak_topics && chapterData.weak_topics.length > 0 && (
                        <div key={chapterNum} className="mb-4">
                          <h3 className="font-bold text-stone-700 mb-2">Chapter {chapterNum}</h3>
                          <ul className="space-y-2">
                            {chapterData.weak_topics.map((topic, index) => (
                              <motion.li
                                key={index}
                                whileHover={{ x: 5 }}
                                className="bg-red-50/70 p-3 rounded-lg border border-red-200/50"
                              >
                                <div className="font-medium text-red-700">{topic.topic}</div>
                                <div className="text-sm text-stone-600">
                                  {topic.details.join(', ')}
                                </div>
                              </motion.li>
                            ))}
                          </ul>
                        </div>
                      )
                    ))}
                  </div>
                ) : (
                  <p className="text-stone-600 italic">No weak topics identified yet</p>
                )}
              </motion.div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
